import _ from "lodash";

// generated by prepare script
export const part1 = (arr) => {
  let lows = 0;

  arr.forEach((line, y) =>
    line.forEach((currVal, x) => {
      const neighbors = [
        arr[y - 1]?.[x],
        arr[y + 1]?.[x],
        arr[y]?.[x - 1],
        arr[y]?.[x + 1],
      ].filter((n) => n !== undefined);

      const isLow = neighbors.every((n) => currVal < n);

      if (isLow) lows += 1 + currVal;
    })
  );

  return lows;
};

export const part2 = (input) => {
  const mappedInput = input.map((row, y) =>
    row.map((val, x) => ({ x, y, val }))
  );

  const lowPoints = [];

  mappedInput.forEach((row, y) => {
    row.forEach((pos, x) => {
      const neighbors = [
        mappedInput[y - 1]?.[x],
        mappedInput[y + 1]?.[x],
        mappedInput[y]?.[x - 1],
        mappedInput[y]?.[x + 1],
      ].filter((n) => n !== undefined);

      const isLowPoint = neighbors.every((n) => pos.val < n.val);

      if (isLowPoint) {
        lowPoints.push(pos);
      } else if (pos.val < 9) {
        pos.lowers = neighbors.filter((n) => n.val < pos.val);
      }
    });
  });

  const nonLowPoints = _.flatten(mappedInput)
    .sort((a, b) => a.val - b.val)
    .filter((p) => p.val !== 9 && !lowPoints.includes(p));

  const basins = lowPoints.map((lp) => {
    let positions = new Set([lp]);

    nonLowPoints.forEach((fmp) => {
      const isInBasin = fmp.lowers.some((lp) => positions.has(lp));
      if (isInBasin) positions.add(fmp);
    });

    return positions.size;
  });

  return basins
    .sort((a, b) => b - a)
    .slice(0, 3)
    .reduce((t, v) => t * v);
};
